import "./App.css"
import { useState, useEffect } from "react"
import axios from "axios"

function App() {
  const [total, setTotal] = useState(0)
  const [cartItems, setCartItems] = useState({})
  const [items, setItems] = useState([])

  const addItemToCart = (price, itemName) => {
    if (price === "" || itemName === "") {
      return
    }
    const floatPrice = parseFloat(price)
    setTotal(total + floatPrice)

    let cartItemCopy = { ...cartItems }
    if (itemName in cartItemCopy) {
      cartItemCopy[itemName] += 1
    } else {
      cartItemCopy[itemName] = 1
    }
    setCartItems(cartItemCopy)
  }

  useEffect(() => {
    // grab all items from mongoDB, using creating API
    // backend server must be running to be able to retrieve items
    axios
      .get("http://localhost:8080/all_items")
      .then(function (response) {
        // handle success
        console.log(response.data)
        setItems(response.data)
      })
      .catch(function (error) {
        // handle error
        console.log(error)
      })
  }, [])

  return (
    <div>
      <div className="cart">
        <div className="my_cart_title">My cart</div>

        <table>
          <tbody>
            <tr>
              <th>Item Name</th>
              <th>Qty</th>
            </tr>
            {Object.keys(cartItems).map((name, i) => {
              return (
                <tr key={i}>
                  <td>{name}</td>
                  <td>{cartItems[name]}</td>
                </tr>
              )
            })}
            <tr>
              <td></td>
              <td>Cart total: ${total.toFixed(2)}</td>
            </tr>
          </tbody>
        </table>

        <form className="card_form" action="http://localhost:8080/place_order" method="post">

          <div>
          <label htmlFor="card_number">Card #: </label>
          <input type="text" name="card_number" id="card_number" />
          </div>
          <div>
          <input type="submit" value="Place Order" />
          </div>
        </form>
      </div>

      <h1>Starbuzz coffee beverages</h1>

      {items
        ? items.map((elem, i) => {
            return (
              <div key={i}>
                <h2>
                  {elem.name} ${elem.price}
                </h2>
                <p>{elem.desc}</p>
                <div
                  className="add_to_cart_btn"
                  onClick={() => addItemToCart(elem.price, elem.name)}
                >
                  Add to cart
                </div>
              </div>
            )
          })
        : null}
    </div>
  )
}

export default App
