import "./App.css"
import { useState, useEffect } from "react"

function twoDecimals(x) {
  return Number.parseFloat(x).toFixed(2)
}

function App() {
  const [total, setTotal] = useState(0)
  const [cartItems, setCartItems] = useState({})

  const addItemToCart = (price, itemName) => {
    const cartItemsClone = { ...cartItems }

    if (itemName in cartItemsClone) {
      cartItemsClone[itemName] += 1
    } else {
      cartItemsClone[itemName] = 1
    }
    setCartItems(cartItemsClone)
    setTotal(total + price)
  }
  return (
    <div>
      <div className="cart">
        <div>Your cart</div>
        {Object.keys(cartItems).map((key, i) => {
          return (
            <div>
              <div>{key}</div>
              <div></div>
            </div>
          )
        })}
        <div>Cart total: ${total.toFixed(2)}</div>
      </div>

      <h1>Starbuzz coffee beverages</h1>
      <div>
        <h2>House Blend, $1.49</h2>
        <p> A smooth, mild blend of coffee</p>
        <div
          className="add_to_cart_btn"
          onClick={() => addItemToCart(1.49, "House Blend")}
        >
          Add to cart
        </div>
      </div>
      <div>
        <h2>Mocha Caffe Latte, $2.35</h2>
        <p>Espresso, steamed with milk and chocolate syrup</p>
        <div
          className="add_to_cart_btn"
          onClick={() => addItemToCart(2.35, "Mocha Caffe Latte")}
        >
          Add to cart
        </div>
      </div>
      <div>
        <h2>Cappucino, $1.89</h2>
        <p>A mixture of espresso, steamed milk, and milk foam</p>
        <div
          className="add_to_cart_btn"
          onClick={() => addItemToCart(1.89, "Cappucino")}
        >
          Add to cart
        </div>
      </div>
      <div>
        <h2>Chai Tea, $1.85</h2>
        <p>A drink made with black tea, milk, and honey</p>
        <div
          className="add_to_cart_btn"
          onClick={() => addItemToCart(1.85, "Chai Tea")}
        >
          Add to cart
        </div>
      </div>

      <form action="http://localhost:8080/place_order" method="post">
        <label htmlFor="card_number">Card #</label>
        <input type="text" name="card_number" id="card_number" />
        <input type="submit" value="Place Order" />
      </form>
    </div>
  )
}

export default App
