import "./App.css"
import { useState, useEffect } from "react"
import axios from "axios"

function App() {
  const [total, setTotal] = useState(0)
  const [cartItems, setCartItems] = useState({})
  const [items, setItems] = useState([])

  const addItemToCart = (price, itemName) => {
    if (price === "" || itemName === "") {
      return
    }
    const twoDecimals = price.toFixed(2)
    const cartItemsClone = { ...cartItems }

    if (itemName in cartItemsClone) {
      cartItemsClone[itemName] += 1
    } else {
      cartItemsClone[itemName] = 1
    }
    console.log(typeof total)
    console.log(typeof price)
    setCartItems(cartItemsClone)
    setTotal(total + price)
  }

  useEffect(() => {
    axios
      .get("http://localhost:8080/all_items")
      .then(function (response) {
        // handle success
        console.log(response.data)
        setItems(response.data)
      })
      .catch(function (error) {
        // handle error
        console.log(error)
      })
  }, [])

  return (
    <div>
      <div className="cart">
        <div>My cart</div>

        <table>
          <tbody>
            <tr>
              <th>Item Name</th>
              <th>Qty</th>
            </tr>
            {Object.keys(cartItems).map((name, i) => {
              return (
                <tr key={i}>
                  <td>{name}</td>
                  <td>{cartItems[name]}</td>
                </tr>
              )
            })}
            <tr>
              <td></td>
              <td>Cart total: ${total}</td>
            </tr>
          </tbody>
        </table>
      </div>

      <h1>Starbuzz coffee beverages</h1>


      {items
        ? items.map((elem, i) => {
            return (
              <div key={i}>
                <h2>{elem.name} ${elem.price}</h2>
                <p>{elem.desc}</p>
                <div
                  className="add_to_cart_btn"
                  onClick={() => addItemToCart(elem.price, elem.name)}
                >
                  Add to cart
                </div>
              </div>
            )
          })
        : null}

      <form action="http://localhost:8080/place_order" method="post">
        <label htmlFor="card_number">Card #</label>
        <input type="text" name="card_number" id="card_number" />
        <input type="submit" value="Place Order" />
      </form>
    </div>
  )
}

export default App
